import http from 'node:http';
import url from 'node:url';

const users = ['Aras', 'Arsy', 'Dimas', 'Ivan', 'Rafy', 'Gilang'];
const MISSING = 4;

const server = http.createServer((req, res) => {
  const { pathname } = url.parse(req.url);
  let id = pathname.match(/^\/(\d+)$/);
  if (!id) {
   res.statusCode = 400;
   return void res.end();
  }

  id = Number(id[1]);

  if (id === MISSING) {
   res.statusCode = 404;
   return void res.end();
  }

  res.setHeader('Content-Type', 'application/json');

  res.end(JSON.stringify({
   id,
   name: users[id % users.length],
  }));
});

server.listen(process.env.PORT || 0, () => {
  const { port } = server.address();
  console.log(`User service listening on localhost on port: ${port}`);
});

const {
ORDER_SERVICE_PORT = 4000,
USER_SERVICE_PORT = 5000,
} = process.env;

const orderService = `http://localhost:${ORDER_SERVICE_PORT}`;
const userService = `http://localhost:${USER_SERVICE_PORT}`;

app.get('/:id', async (req, res, next) => {
  const { id } = req.params;
  try {
    const [orderRes, userRes] = await Promise.all([
      fetch(`${orderService}/${id}`),
      fetch(`${userService}/${id}`),
    ]);
    if (!orderRes.ok) {
      if (orderRes.status === 400) return res.status(400).json({ message: 'bad request' });
      if (orderRes.status === 404) return res.status(404).json({ message: 'not found' });
      throw new Error(`Order service error: ${orderRes.status}`);
    }
    if (!userRes.ok) {
      if (userRes.status === 400) return res.status(400).json({ message: 'bad request' });
      if (userRes.status === 404) return res.status(404).json({ message: 'not found' });
      throw new Error(`User service error: ${userRes.status}`);
    }
    const order = await orderRes.json();
    const user = await userRes.json();
    res.json({
      id: order.id,
      menu: order.menu,
      user: user.name,
    });
  } catch (err) {
    next(err);
  }
});

import express from 'express';

const {
  ORDER_SERVICE_PORT = 4000,
  USER_SERVICE_PORT = 5000,
} = process.env;

const orderService = `http://localhost:${ORDER_SERVICE_PORT}`;
const userService = `http://localhost:${USER_SERVICE_PORT}`;

const app = express();
const PORT = 3000;

app.use(express.json());

app.get('/:id', async (req, res, next) => {
  const { id } = req.params;

  try {
    const [orderRes, userRes] = await Promise.all([
      fetch(`${orderService}/${id}`),
      fetch(`${userService}/${id}`),
    ]);

    if (!orderRes.ok) {
      if (orderRes.status === 400) return res.status(400).json({ message: 'bad request' });
      if (orderRes.status === 404) return res.status(404).json({ message: 'not found' });
      throw new Error(`Order service error: ${orderRes.status}`);
    }

    if (!userRes.ok) {
      if (userRes.status === 400) return res.status(400).json({ message: 'bad request' });
      if (userRes.status === 404) return res.status(404).json({ message: 'not found' });
      throw new Error(`User service error: ${userRes.status}`);
    }

    const order = await orderRes.json();
    const user = await userRes.json();

    res.json({
      id: order.id,
      menu: order.menu,
      user: user.name,
    });
  } catch (err) {
    next(err);
  }
});

app.use((err, req, res, next) => {
  console.error(err);
  res.status(500).json({ message: 'internal server error' });
});

app.listen(PORT, () => {
  console.log(`Server jalan di http://localhost:${PORT}`);
});

